import './App.css';
import React from 'react';
import { useState } from 'react';

function Upgrade(props) {

  const [cps, setcps] = useState(0);

  function autoClickBuy() {
    if (App.score >= 10) {
        App.setscore(props.score - 10);
        setcps(cps + 1);
    }
  }

  return (
    <div className='upgradeList'>
        <h1>Upgrades:</h1>
        <div>
            <button onClick={props.setScore}>Auto Clicker </button>
        </div>
    </div>
  );
}

export default Upgrade;
